mapper.insert:
    INSERT INTO exam.answer (
answer, 
user_id, 
question_id
    )
    VALUES (
#{answer}, 
#{userId}, 
#{questionId}
    );

mapper.update:
    UPDATE exam.answer
    SET
answer = #{answer}, 
user_id = #{userId}, 
question_id = #{questionId}
    WHERE id = #{id}

mapper.select:
SELECT * FROM exam.answer
id = #{id}
AND answer LIKE "%"#{answer}"%" AND user_id LIKE "%"#{userId}"%" AND question_id LIKE "%"#{questionId}"%"
