mapper.insert:
INSERT INTO exam.answer (
    answer,
    user_id,
    question_id
)
VALUES (
    #{answer},
    #{userId},
    #{questionId}
);

mapper.update:
UPDATE exam.answer
SET
    answer      = #{answer},
    user_id     = #{userId},
    question_id = #{questionId}
WHERE id = #{id}

mapper.select:
SELECT
a.id,
a.answer,
a.user_id as userId,
a.question_id as questionId
FROM exam.answer a


        id = #{id}

    AND answer = #{answer}
    AND user_id = #{userId}
    AND question_id = #{questionId}
