mapper.insert:
    INSERT INTO exam.message (
name, 
content, 
time
    )
    VALUES (
#{name}, 
#{content}, 
#{time}
    );

mapper.update:
    UPDATE exam.message
    SET
name = #{name}, 
content = #{content}, 
time = #{time}
    WHERE id = #{id}

mapper.select:
SELECT * FROM exam.message
id = #{id}
AND name LIKE "%"#{name}"%" AND content LIKE "%"#{content}"%" AND time LIKE "%"#{time}"%"
