mapper.insert:
INSERT INTO exam.word (
    english,
    chinese,
    sentence
)
VALUES (
    #{english},
    #{chinese},
    #{sentence}
);

mapper.update:
UPDATE exam.word
SET
    english  = #{english},
    chinese  = #{chinese},
    sentence = #{sentence}
WHERE id = #{id}

mapper.select:
SELECT * FROM exam.word
id = #{id}
AND english LIKE "%"#{english}"%" AND chinese LIKE "%"#{chinese}"%" AND sentence LIKE "%"#{sentence}"%"
